"""Bitflags indicating EKF estimator status."""

from enum import IntEnum


class EkfStatus(IntEnum):
    """Autogenerated from EKF_STATUS_FLAGS entries starting with `EKF_`."""

    ATTITUDE = 1
    CONST_POS_MODE = 128
    GPS_GLITCHING = 32768
    POS_HORIZ_ABS = 16
    POS_HORIZ_REL = 8
    POS_VERT_ABS = 32
    POS_VERT_AGL = 64
    PRED_POS_HORIZ_ABS = 512
    PRED_POS_HORIZ_REL = 256
    STATUS_FLAGS_ENUM_END = 32769
    UNINITIALIZED = 1024
    VELOCITY_HORIZ = 2
    VELOCITY_VERT = 4
